class OddGenerator:    def __init__(self):        self.first_number = 1        self.max = 10    def __iter__(self):        self.value = 1        return self    def __next__(self):        if self.value > self.max:            raise StopIteration        else:            self.value = self.value + 2            return self.valueoddGenerator = OddGenerator()for j in oddGenerator:    print(j)