import socketHOST = "127.0.0.1"PORT = 65432with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:    s.bind((HOST, PORT))    s.listen(1)    conn, addr = s.accept()    with conn:        print('connected by', addr)        while True:            data = conn.recv(1024)            if not data:                break            input_value = input()            output = data.decode('UTF_8') + "I recive your message which is /n " + "This is my message "            conn.sendall(output.encode('UTF_8'))class UDPConnection:    port_number = 4444;        def set_soceket(self):        udpConnection = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)        udpConnection.setsockopt(socket.SOL_SOCKET, socket.SO_BROADCAST)        udpConnection.bind(("", 4444))